import React from 'react';
import Graph from './Graph';


function Chart() {
  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "flex-start",
        marginBottom: "50px",
        gap: "20px",
      }}
    >
      {/* Left side content */}
      <div
        style={{
          width: "300px",
          padding: "20px",
          borderRadius: "10px",
          height: "500px",
          textAlign: "left",
          display: "flex",
          flexDirection: "column",
          gap: "10px",
          cursor: "pointer", // shows pointer on hover
        }}
      >
        <h2
          style={{
            fontSize: "28px",
            fontWeight: "700",
            color: "#1F2937", // dark gray for a clean look
            marginBottom: "20px", // spacing below heading
            lineHeight: "1.3", // improves readability
            borderBottom: "2px solid #000a14ff", // subtle underline accent
            paddingBottom: "20px",
          }}
        >
          <span >Evalueserve Tech Index 50</span>
        </h2>
        <br></br>

        {/* Clickable text items */}
        <div
          onClick={() => alert(" clicked")}
          style={{
            fontSize: "28px",
            fontWeight: "200",
            transition: "0.2s",
            color: "#333",
          }}
          onMouseOver={(e) => (e.currentTarget.style.color = "#007BFF")}
          onMouseOut={(e) => (e.currentTarget.style.color = "#333")}
        >
          → Factsheet
        </div>
        <br></br>
        <div
          onClick={() => alert(" clicked")}
          style={{
            fontSize: "30px",
            fontWeight: "200",
            transition: "0.2s",
            color: "#333",
          }}
          onMouseOver={(e) => (e.currentTarget.style.color = "#007BFF")}
          onMouseOut={(e) => (e.currentTarget.style.color = "#333")}
        >
          → Methodolgy
        </div>
        <br></br>
        <div
          onClick={() => alert(" clicked")}
          style={{
            fontSize: "30px",
            fontWeight: "200",
            transition: "0.2s",
            color: "#333",
            whiteSpace: "nowrap",
          }}
          onMouseOver={(e) => (e.currentTarget.style.color = "#007BFF")}
          onMouseOut={(e) => (e.currentTarget.style.color = "#333")}
        >
          → Index Constituent
        </div>
      </div>

      {/* Right side: Graph */}
      <div
        style={{
          width: "900px",
          height: "500px",
          backgroundColor: "#FFF7F1",
          borderRadius: "10px",
          boxShadow: "0 8px 30px rgba(0,0,0,0.35)",
          padding: "20px",
          marginLeft: "150px",
        }}
      >
        <Graph />
      </div>
    </div>
  );
}

export default Chart;
