import Card from 'react-bootstrap/Card';
import 'bootstrap/dist/css/bootstrap.css';

export default function Cards({title, image}) {
  return (
    <Card style={{ width: '40rem', height: '25rem', borderRadius: '30px'}}>
      <Card.Body>
      {
        image && 
          <img src={image} alt="..." style={{overflow: 'hidden', height: '53%', width: '100%'}}/>
      }
        <Card.Title><h3>{title}</h3></Card.Title>
        <Card.Text>
          <b>Find out moreâ†’</b>
        </Card.Text>
      </Card.Body>
    </Card>
  );
}
